import { Component, DoCheck, OnDestroy, OnInit } from '@angular/core';
import { LoginService } from '../services/login.service';
import { Login } from '../models/login';
import { Subscription } from 'rxjs';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent {
  loginData: Login = new Login('', '');
  errorMessage: string = '';
  hasError: boolean = false;
  isLoading: boolean = false;
  constructor(
    private loginService: LoginService,
    private route: Router,
    private router: ActivatedRoute
  ) {}

  ngOnInit(): void {}

  onLogin() {
    this.isLoading = true;
    this.loginService.login(this.loginData).subscribe({
      next: (responseData: any) => {
        console.log(responseData);
        if (responseData.result.IsSuccess) {
          localStorage.setItem('userToken', responseData.result);
          this.loginService.loggedIn.next(true);
          this.route.navigate(['/items'], { relativeTo: this.router });
          this.hasError = false;
          this.isLoading = false;
        } else {
          this.errorMessage = 'Invalid username or password!';
          this.hasError = true;
          this.isLoading = false;
        }
      },
      error: (err) => {
        this.errorMessage = 'Invalid username or password!';
        this.hasError = true;
        this.isLoading = false;

        console.clear();
      },
    });
  }
}
<div class="container mt-5">
  <div class="row justify-content-center">
    <div class="col-md-4 col-10">
      <form class="form mt-5 mb-5 py-4" method="post">
        <div class="container p-4">
          <p class="form-title">Sign in to your account</p>
          <div class="row text-center">
            <div>
              <app-spinner *ngIf="isLoading"></app-spinner>
              <ul
                class="text-danger"
                style="list-style-type: none; padding-left: 0"
              >
                <li *ngIf="hasError">{{ errorMessage }}</li>
              </ul>
            </div>
          </div>
          <div class="input-container">
            <input
              name="username"
              type="text"
              class="form-control me-5"
              placeholder="Username..."
              [(ngModel)]="loginData.UserName"
            />
          </div>
          <div class="input-container">
            <input
              name="password"
              type="password"
              id="inputPassword"
              class="form-control"
              placeholder="Password..."
              [(ngModel)]="loginData.Password"
            />
          </div>
          <button class="submit col-12 mt-4" type="submit" (click)="onLogin()">
            Sign in
          </button>
        </div>
      </form>
    </div>
  </div>
</div>
