import { Component, Input } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Product } from 'src/app/models/Product';
import { ItemService } from 'src/app/services/item.service';
import { LoginService } from 'src/app/services/login.service';
import { environment } from 'src/environments/environment.development';
import { Location } from '@angular/common';

@Component({
  selector: 'app-itemcard',
  templateUrl: './itemcard.component.html',
  styleUrls: ['./itemcard.component.css'],
})
export class ItemcardComponent {
  @Input() item: Product;
  imgUrl: string;
  showModal: boolean = false;
  hasPermission: boolean = false;

  constructor(
    private itemservice: ItemService,
    private route: ActivatedRoute,
    private router: Router,
    private loginService: LoginService,
    private location: Location
  ) {}
  ngOnInit() {
    this.imgUrl = environment.apiDomain + '/' + this.item.imgUrl;
    this.hasPermission = this.loginService.hasPermission;
  }
  onUpdateItem() {
    this.router.navigate([`create/${this.item.id}`], {
      relativeTo: this.route,
    });
  }
  deleteItem(id: number) {
    this.showModal = false;
    this.itemservice.DeleteItem(id).subscribe({
      next: (response) => {
        console.log(response);
      },
    });
  }

  say() {
    this.showModal = true;
  }

  onGetSingleItem(id: number) {
    this.router.navigate([`${id}`], { relativeTo: this.route });
  }
}
<div class="col">
  <div class="card h-100 shadow-sm bg-light">
    <a
      class="my-card-link d-flex flex-column flex-grow-1 justify-content-between mb-auto"
    >
      <img
        *ngIf="!item.imgUrl"
        src="../../../assets/images/item1.jpg"
        class="card-img-top mb-auto click-item"
        alt="Fissure in Sandstone"
        (click)="onGetSingleItem(item.id)"
      />
      <img
        *ngIf="item.imgUrl"
        [src]="imgUrl"
        class="card-img-top mb-auto click-item"
        alt="Fissure in Sandstone"
        (click)="onGetSingleItem(item.id)"
      />
      <div class="card-body flex-grow-0 flex-shrink-0 p-2">
        <h5
          class="card-title fw-bold click-item"
          (click)="onGetSingleItem(item.id)"
        >
          {{ item.name }}
        </h5>
        <p class="card-text lh-1">{{ item.description }}</p>
        <h4>
          <span class="badge bg-primary">{{ item.price }}&nbsp;&dollar;</span>
        </h4>
      </div>
    </a>
    <div class="d-flex align-self-center mb-3 mt-1">
      <button
        *ngIf="hasPermission"
        type="button"
        class="btn btn-success mb-1 me-5"
        (click)="onUpdateItem()"
      >
        <span>
          <i class="fa fa-pencil-square-o btn-success"></i>
        </span>
      </button>
      <app-deleting-modal
        *ngIf="hasPermission"
        [type]="'item'"
        [id]="item.id"
      ></app-deleting-modal>
    </div>
  </div>
</div>
