import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { environment } from 'src/environments/environment.development';
import { Result } from '../interfaces/result.interface';
import { Category } from '../models/category';
import { Subject } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class CategoryService {
  pageSize: number = 9;
  refresh: Subject<void> = new Subject<void>();

  private url: string = environment.apiDomain + '/api/Category';
  constructor(private http: HttpClient) {}

  getAllCategories(pageNumber: number = 1, search: string = '') {
    return this.http.get<Result>(
      this.url +
        `/GetAllCategories?pageSize=${this.pageSize}&pageNumber=${pageNumber}&search=${search}`
    );
  }

  PostCategory(Category: Category) {
    return this.http.post(this.url, Category);
  }
  PutCategory(Category: Category) {
    return this.http.put(`${this.url}/${Category.id}`, Category);
  }
  DeleteCategory(id: number) {
    return this.http.delete(`${this.url}/${id}`);
  }

  GetCategory(id: number) {
    return this.http.get(`${this.url}/${id}`);
  }
}
