import {
  Component,
  EventEmitter,
  Input,
  OnChanges,
  OnInit,
  Output,
  SimpleChanges,
} from '@angular/core';
import { ItemService } from 'src/app/services/item.service';

@Component({
  selector: 'app-pagination',
  templateUrl: './pagination.component.html',
  styleUrls: ['./pagination.component.css'],
})
export class PaginationComponent implements OnInit, OnChanges {
  @Input() totalCount: number = 30;
  @Input() currentPage: number = 1;
  @Output() changeCurrentPage: EventEmitter<number> =
    new EventEmitter<number>();

  totalPages: number = 30;
  // Adding Dots in case of more than 10 pages
  startPage: number = Math.max(1, this.currentPage - 4);
  endPage: number = Math.min(this.totalPages, this.startPage + 9);
  ngOnInit() {}
  ngOnChanges(changes: SimpleChanges): void {
    this.totalPages = Math.ceil(this.totalCount / 9);
    this.startPage = Math.max(1, this.currentPage - 4);
    this.endPage = Math.min(this.totalPages, this.startPage + 9);
  }

  onChangeCurrentPage(page: number) {
    this.currentPage = page;
    this.startPage = Math.max(1, this.currentPage - 4);
    this.endPage = Math.min(this.totalPages, this.startPage + 9);
    this.changeCurrentPage.emit(page);
  }
}
<div class="pagination justify-content-center pt-3 pb-3">
  <a
    class="paginationbtn btn show-on-mobile"
    [ngClass]="{ disabled: currentPage == 1 }"
    (click)="onChangeCurrentPage(currentPage - 1)"
    aria-label="Previous"
  >
    <svg
      xmlns="http://www.w3.org/2000/svg"
      class="btn-icon"
      fill="none"
      viewBox="0 0 24 24"
      stroke="currentColor"
      stroke-width="2"
    >
      <path
        stroke-linecap="round"
        stroke-linejoin="round"
        d="M15 19l-7-7 7-7"
      />
    </svg>
  </a>

  <a
    *ngIf="startPage > 1"
    (click)="onChangeCurrentPage(1)"
    class="page-item hide-on-mobile page-link"
    >1</a
  >

  <span *ngIf="startPage > 2" class="dots hide-on-mobile">...</span>

  <a
    *ngFor="let item of [].constructor(endPage - startPage); let i = index"
    class="page-item hide-on-mobile page-link"
    (click)="onChangeCurrentPage(i + startPage)"
    [ngClass]="{ 'page-link-current': i + startPage == currentPage }"
    >{{ i + startPage }}</a
  >

  <span *ngIf="endPage < totalPages" class="dots hide-on-mobile">...</span>
  <a
    *ngIf="endPage <= totalPages"
    (click)="onChangeCurrentPage(totalPages)"
    class="page-item hide-on-mobile page-link"
    >{{ totalPages }}</a
  >

  <a
    class="paginationbtn btn show-on-mobile"
    (click)="onChangeCurrentPage(currentPage + 1)"
    [ngClass]="{ disabled: currentPage == totalPages }"
    aria-label="Next"
  >
    <svg
      xmlns="http://www.w3.org/2000/svg"
      class="btn-icon"
      fill="none"
      viewBox="0 0 24 24"
      stroke="currentColor"
      stroke-width="2"
    >
      <path stroke-linecap="round" stroke-linejoin="round" d="M9 5l7 7-7 7" />
    </svg>
  </a>
</div>
